package org.example;

public class DescontoFixo extends DescontoDecorator {
    private double valorFixo;

    public DescontoFixo(Desconto desconto, double valorFixo) {
        super(desconto);
        this.valorFixo = valorFixo;
    }

    @Override
    public double aplicarDesconto(double valor) {
        double valorComDesconto = desconto.aplicarDesconto(valor) - valorFixo;
        return Math.max(0, valorComDesconto);
    }
}

package org.example;

public class Endereco implements Cloneable {
    private String rua;
    private String numero;
    private String cidade;
    private String estado;
    private String cep;

    public Endereco(String rua, String numero, String cidade, String estado, String cep) {
        this.rua = rua;
        this.numero = numero;
        this.cidade = cidade;
        this.estado = estado;
        this.cep = cep;
    }

    public String getRua() { return rua; }
    public void setRua(String rua) { this.rua = rua; }
    public String getNumero() { return numero; }
    public void setNumero(String numero) { this.numero = numero; }
    public String getCidade() { return cidade; }
    public void setCidade(String cidade) { this.cidade = cidade; }
    public String getEstado() { return estado; }
    public void setEstado(String estado) { this.estado = estado; }
    public String getCep() { return cep; }
    public void setCep(String cep) { this.cep = cep; }

    @Override
    public Endereco clone() {
        try {
            return (Endereco) super.clone();
        } catch (CloneNotSupportedException e) {
            throw new AssertionError();
        }
    }
}

