public class Endereco implements Cloneable {
    private String rua;
    private String numero;
    private String cidade;
    private String estado;
    private String cep;

    private Endereco(Builder builder) {
        this.rua = builder.rua;
        this.numero = builder.numero;
        this.cidade = builder.cidade;
        this.estado = builder.estado;
        this.cep = builder.cep;
    }

    public static class Builder {
        private String rua;
        private String numero;
        private String cidade;
        private String estado;
        private String cep;

        public Builder setRua(String rua) { this.rua = rua; return this; }
        public Builder setNumero(String numero) { this.numero = numero; return this; }
        public Builder setCidade(String cidade) { this.cidade = cidade; return this; }
        public Builder setEstado(String estado) { this.estado = estado; return this; }
        public Builder setCep(String cep) { this.cep = cep; return this; }
        public Endereco build() { return new Endereco(this); }
    }

    public String getRua() { return rua; }
    public void setRua(String rua) { this.rua = rua; }
    public String getNumero() { return numero; }
    public void setNumero(String numero) { this.numero = numero; }
    public String getCidade() { return cidade; }
    public void setCidade(String cidade) { this.cidade = cidade; }
    public String getEstado() { return estado; }
    public void setEstado(String estado) { this.estado = estado; }
    public String getCep() { return cep; }
    public void setCep(String cep) { this.cep = cep; }

    @Override
    public Endereco clone() {
        try {
            return (Endereco) super.clone();
        } catch (CloneNotSupportedException e) {
            throw new AssertionError();
        }
    }
}

package org.example;

import java.util.Observable;

public class Pedido extends Observable {
    private PedidoEstado processoAtual;
    private Usuario usuario;
    private FormaPagamento formaPagamento;
    private String mensagem;
    private double valorTotal;
    private Desconto desconto;

    private Pedido(Builder builder) {
        this.usuario = builder.usuario;
        this.formaPagamento = builder.formaPagamento;
        this.valorTotal = builder.valorTotal;
        this.mensagem = builder.mensagem;
        this.desconto = builder.desconto;
        this.processoAtual = PedidoEmProcesso.getInstance();
    }

    public static class Builder {
        private Usuario usuario;
        private FormaPagamento formaPagamento;
        private double valorTotal;
        private String mensagem;
        private Desconto desconto;

        public Builder setUsuario(Usuario usuario) {
            this.usuario = usuario;
            return this;
        }
        public Builder setFormaPagamento(FormaPagamento formaPagamento) {
            this.formaPagamento = formaPagamento;
            return this;
        }
        public Builder setValorTotal(double valorTotal) {
            this.valorTotal = valorTotal;
            return this;
        }
        public Builder setMensagem(String mensagem) {
            this.mensagem = mensagem;
            return this;
        }
        public Builder setDesconto(Desconto desconto) {
            this.desconto = desconto;
            return this;
        }
        public Pedido build() {
            return new Pedido(this);
        }
    }

    public PedidoEstado getProcessoAtual() {
        return processoAtual;
    }
    public void setProcessoAtual(PedidoEstado processoAtual) {
        this.processoAtual = processoAtual;
    }
    public void updateEstado(){
        setChanged();
        notifyObservers();
    }
    public double pagamento(double valorTotal){
        return valorTotal * formaPagamento.getTaxa();
    }
    public String getMensagem() {
        return mensagem;
    }
    public void setMensagem(String mensagem) {
        this.mensagem = mensagem;
    }
    @Override
    public String toString() {
        return mensagem;
    }
    public Usuario getUsuario() {
        return usuario;
    }
    public FormaPagamento getFormaPagamento() {
        return formaPagamento;
    }
    public double getValorTotal() {
        return valorTotal;
    }
    public Desconto getDesconto() {
        return desconto;
    }

    public void configurarCadeiaEstados() {
        PedidoEmProcesso.getInstance().setProximoEstadoNaCadeia(PedidoEmPreparo.getInstance());
        PedidoEmPreparo.getInstance().setProximoEstadoNaCadeia(PedidoEmTransporte.getInstance());
        PedidoEmTransporte.getInstance().setProximoEstadoNaCadeia(PedidoEntregue.getInstance());
    }

    public String processarCadeiaEstados() {
        PedidoEstado estado = PedidoEmProcesso.getInstance();
        String resultado = null;
        while (estado != null) {
            resultado = estado.processar(this);
            estado = estado.getProximoEstadoNaCadeia();
        }
        return resultado;
    }

    public double calcularValor(FormaPagamento formaPagamento) {
        double valorComDesconto = desconto != null ? desconto.aplicarDesconto(valorTotal) : valorTotal;
        return valorComDesconto + (valorComDesconto * formaPagamento.getTaxa());
    }
}

package org.example;

import java.util.Observable;

public class Pedido extends Observable {
    private PedidoEstado processoAtual;
    private Usuario usuario;
    private FormaPagamento formaPagamento;
    private String mensagem;
    private double valorTotal;
    private Desconto desconto;

    private Pedido(Builder builder) {
        this.usuario = builder.usuario;
        this.formaPagamento = builder.formaPagamento;
        this.valorTotal = builder.valorTotal;
        this.mensagem = builder.mensagem;
        this.desconto = builder.desconto;
        this.processoAtual = PedidoEmProcesso.getInstance();
    }

    public static class Builder {
        private Usuario usuario;
        private FormaPagamento formaPagamento;
        private double valorTotal;
        private String mensagem;
        private Desconto desconto;

        public Builder setUsuario(Usuario usuario) {
            this.usuario = usuario;
            return this;
        }
        public Builder setFormaPagamento(FormaPagamento formaPagamento) {
            this.formaPagamento = formaPagamento;
            return this;
        }
        public Builder setValorTotal(double valorTotal) {
            this.valorTotal = valorTotal;
            return this;
        }
        public Builder setMensagem(String mensagem) {
            this.mensagem = mensagem;
            return this;
        }
        public Builder setDesconto(Desconto desconto) {
            this.desconto = desconto;
            return this;
        }
        public Pedido build() {
            return new Pedido(this);
        }
    }

    public PedidoEstado getProcessoAtual() {
        return processoAtual;
    }
    public void setProcessoAtual(PedidoEstado processoAtual) {
        this.processoAtual = processoAtual;
    }
    public void updateEstado(){
        setChanged();
        notifyObservers();
    }
    public double pagamento(double valorTotal){
        return valorTotal * formaPagamento.getTaxa();
    }
    public String getMensagem() {
        return mensagem;
    }
    public void setMensagem(String mensagem) {
        this.mensagem = mensagem;
    }
    @Override
    public String toString() {
        return mensagem;
    }
    public Usuario getUsuario() {
        return usuario;
    }
    public FormaPagamento getFormaPagamento() {
        return formaPagamento;
    }
    public double getValorTotal() {
        return valorTotal;
    }
    public Desconto getDesconto() {
        return desconto;
    }

    public void configurarCadeiaEstados() {
        PedidoEmProcesso.getInstance().setProximoEstadoNaCadeia(PedidoEmPreparo.getInstance());
        PedidoEmPreparo.getInstance().setProximoEstadoNaCadeia(PedidoEmTransporte.getInstance());
        PedidoEmTransporte.getInstance().setProximoEstadoNaCadeia(PedidoEntregue.getInstance());
    }

    public String processarCadeiaEstados() {
        PedidoEstado estado = PedidoEmProcesso.getInstance();
        String resultado = null;
        while (estado != null) {
            resultado = estado.processar(this);
            estado = estado.getProximoEstadoNaCadeia();
        }
        return resultado;
    }

    public double calcularValor(FormaPagamento formaPagamento) {
        double valorComDesconto;
        if (desconto != null) {
            valorComDesconto = desconto.aplicarDesconto(valorTotal);
        } else {
            valorComDesconto = valorTotal;
        }
        return valorComDesconto + (valorComDesconto * formaPagamento.getTaxa());
    }
}

package org.example;

public class PedidoEmPreparo extends PedidoEstado {
    private PedidoEmPreparo() {}
    private static PedidoEmPreparo instance = new PedidoEmPreparo();
    public static PedidoEmPreparo getInstance() {
        return instance;
    }
    @Override
    protected void processarPedido(Pedido pedido) {
        pedido.setMensagem("Restaurante preparando o pedido.");
    }
    @Override
    protected String finalizarPedido(Pedido pedido) {
        return processarProximo(pedido);
    }
}

package org.example;

public class PedidoEmPreparo extends PedidoEstado {
    private PedidoEmPreparo() {}
    private static PedidoEmPreparo instance = new PedidoEmPreparo();
    public static PedidoEmPreparo getInstance() {
        return instance;
    }
    @Override
    protected void processarPedido(Pedido pedido) {
        pedido.setMensagem("Restaurante preparando o pedido.");
    }
    @Override
    protected String finalizarPedido(Pedido pedido) {
        if (proximoEstadoNaCadeia != null) {
            return proximoEstadoNaCadeia.processar(pedido);
        }
        return "Pedido entregue ou cancelado.";
    }
}

