import org.example.*;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.*;

public class PedidoTest {
    Usuario usuario;
    Pedido pedido;
    FormaPagamento forma;

    @BeforeEach
    void setUp() {
        usuario = new Usuario.Builder().setNome("Maria").build();
        pedido = new Pedido.Builder().setUsuario(usuario).setValorTotal(100.0).build();
        PedidoEmProcesso.getInstance().setProximoEstadoNaCadeia(PedidoEmPreparo.getInstance());
        PedidoEmPreparo.getInstance().setProximoEstadoNaCadeia(PedidoEmTransporte.getInstance());
        PedidoEmTransporte.getInstance().setProximoEstadoNaCadeia(PedidoEntregue.getInstance());
    }

    @Test
    void testValorComTaxaCartaoCredito() {
        forma = new FormaCartaoCredito();
        double valorFinal = pedido.calcularValorComTaxa(forma);
        assertEquals(105.0, valorFinal, 0.001);
    }

    @Test
    void testValorComTaxaCartaoDebito() {
        forma = new FormaCartaoDebito();
        double valorFinal = pedido.calcularValorComTaxa(forma);
        assertEquals(102.0, valorFinal, 0.001);
    }

    @Test
    void testValorComTaxaPix() {
        forma = new FormaPix();
        double valorFinal = pedido.calcularValorComTaxa(forma);
        assertEquals(101.0, valorFinal, 0.001);
    }

    @Test
    void testCadeiaEstadoPedido() {
        pedido.configurarCadeiaEstados();
        String resultado = pedido.processarCadeiaEstados();
        assertEquals("Pedido entregue ao cliente.", resultado);
    }
}

