package org.example;

import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.*;

public class UsuarioTest {
    @Test
    public void testCloneSuccess() {
        Endereco endereco = new Endereco("Rua A", "123", "CidadeX", "EstadoY", "00000-000");
        Usuario usuarioOriginal = new Usuario("Jo達o", endereco);
        Usuario usuarioClonado = usuarioOriginal.clone();

        assertNotSame(usuarioOriginal, usuarioClonado);
        assertEquals(usuarioOriginal.getNome(), usuarioClonado.getNome());
        assertNotSame(usuarioOriginal.getEndereco(), usuarioClonado.getEndereco());
        assertEquals(usuarioOriginal.getEndereco().getRua(), usuarioClonado.getEndereco().getRua());
    }

    @Test
    public void testCloneInvalidoException() {
        Endereco endereco = new Endereco("Rua B", "456", "CidadeY", "EstadoZ", "11111-111");
        Usuario usuarioOriginal = new Usuario("Maria", endereco);
        Usuario usuarioClonado = usuarioOriginal.clone();
        usuarioClonado.setNome("Nome Diferente");
        if (!usuarioOriginal.getNome().equals(usuarioClonado.getNome())) {
            throw new Usuario.CloneInvalidoException("Nome do clone difere do original");
        }
    }
}
